mapper.select:
SELECT
    ,

FROM
    private_notification pri_no
LEFT JOIN
    member mem ON mem.id = pri_no.member_id


        pri_no.member_id = #{memberId,jdbcType=INTEGER}


        AND pri_no.to_member_id = #{toMemberId,jdbcType=INTEGER}


        AND pri_no.title = #{title,jdbcType=VARCHAR}


        AND pri_no.is_read = #{isRead,jdbcType=INTEGER}


        AND pri_no.time = #{time}


        AND pri_no.access_range = #{accessRange,jdbcType=VARCHAR}


        AND pri_no.content = #{content,jdbcType=LONGVARCHAR}


        AND pri_no.noti_type = #{notiType}



        AND pri_no.noti_type IN
        #{_t}



ORDER BY
    pri_no.time DESC
LIMIT
    #{pageNumber}, #{pageSize}

SELECT
    COUNT(*)
FROM
    private_notification pri_no


        pri_no.member_id = #{memberId,jdbcType=INTEGER}


        AND pri_no.to_member_id = #{toMemberId,jdbcType=INTEGER}


        AND pri_no.title = #{title,jdbcType=VARCHAR}


        AND pri_no.is_read = #{isRead,jdbcType=INTEGER}


        AND pri_no.time = #{time}


        AND pri_no.access_range = #{accessRange,jdbcType=VARCHAR}


        AND pri_no.content = #{content,jdbcType=LONGVARCHAR}


        AND pri_no.noti_type = #{notiType}



        AND pri_no.noti_type IN
        #{_t}
