mapper.select:
SELECT , ,
FROM second_hand_post post LEFT JOIN member mem ON mem.id = post.member_id LEFT JOIN second_hand_post_category cate ON post.second_hand_post_category_id = cate.id WHERE post.second_hand_post_category_id = #{categoryId} AND post.deleted = false ORDER BY post.post_time DESC post.page_view DESC
LIMIT #{pageNumber}, #{pageSize}

SELECT
    ,
    ,

FROM
    second_hand_post post
LEFT JOIN
    member mem ON mem.id = post.member_id
LEFT JOIN
    second_hand_post_category cate ON post.second_hand_post_category_id = cate.id


        post.title LIKE CONCAT('%', #{title,jdbcType=VARCHAR}, '%')


        OR post.expired_time = #{expiredTime}


        OR post.post_time = #{postTime}


        OR post.depreciation_rate = #{depreciationRate,jdbcType=VARCHAR}


        OR post.original_price = #{originalPrice,jdbcType=DECIMAL}


        OR post.sell_price = #{sellPrice,jdbcType=DECIMAL}


        OR post.likes = #{likes,jdbcType=INTEGER}


        OR post.dislikes = #{dislikes,jdbcType=INTEGER}


        OR post.member_id = #{memberId,jdbcType=INTEGER}


        OR post.second_hand_post_category_id = #{secondHandPostCategoryId,jdbcType=INTEGER}


        OR post.complaint = #{complaint,jdbcType=INTEGER}


        OR post.page_view = #{pageView,jdbcType=INTEGER}


        OR post.verified = #{verified,jdbcType=VARCHAR}


        OR post.trade_place LIKE CONCAT('%', #{tradePlace,jdbcType=VARCHAR}, '%')


        OR post.description LIKE CONCAT('%', #{description,jdbcType=LONGVARCHAR}, '%')



        OR post.contact_name = #{contactName}


        OR post.contact_qq = #{contactQq}


        OR post.contact_phone = #{contactPhone}


        OR post.expired = #{expired}

    AND post.deleted = #{deleted}


ORDER BY
    post.post_time DESC
LIMIT
    #{pageNumber}, #{pageSize}

